mapper.select:
select postNo, title, content, writer, regDate, viewCount, sameThinking, uploadFileName, boardNo, (select count(*) from reply where postNo = f.postNo) as replyCount 
from freeBoard f 
where postNo = #{postNo}

mapper.update:
update freeBoard
set title = #{title}, content = #{content}, uploadFileName = #{uploadFileName, jdbcType=VARCHAR} 
where postNo = #{postNo}
